import React, { Component } from 'react';
import CreateDataset from '../dataset/CreateDataset';
import ShowDataset from '../dataset/ShowDataset';
import DeleteDataset from "../dataset/DeleteDataset";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';


class Dataset extends Component {
    constructor(props) {
        super(props);
        this.state = {
            datasetSequence : this.props.datasetSequence,
            datasetOriginalJSON : this.props.datasetOriginalJSON,

            datasetConfigJSON: this.props.datasetOriginalJSON.datasetConfig,
            datasetStatusJSON: this.props.datasetOriginalJSON.status,
            open: false
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
    }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetConfigUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        e.preventDefault();
        console.log(this.state.datasetConfigUserInput)
        this.props.handleSendRequest(this.state.datasetConfigUserInput, this.state.httpMethod)
    }


    handleClickOpen = () => {
        this.setState({ open: true });
    };

    handleClose = () => {
        this.setState({ open: false });
    };


    render() {
        const {datasetSequence, datasetOriginalJSON, datasetConfigJSON} = this.state

        return (
            <div key={datasetSequence}>

                {/*<ShowDataset hasDataset={hasDataset}*/}
                {/*             datasetConfigJSON={datasetOriginalJSON.datasetConfig}*/}
                {/*             datasetStatusJSON={datasetOriginalJSON.status}*/}
                {/*/>*/}


                <div className='DS JSON'>
                    <h3>Original Input JSON:</h3>
                    <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>
                </div>

                <DeleteDataset datasetConfigJSON={datasetConfigJSON} handleSendRequest={this.props.handleSendRequest}/>

                <div className='Delete'>
                    <Button variant="outlined" onClick={handleClickOpen}>
                        Open alert dialog
                    </Button>
                    <Dialog
                        open={open}
                        onClose={handleClose}
                        aria-labelledby="alert-dialog-title"
                        aria-describedby="alert-dialog-description"
                    >
                        <DialogTitle id="alert-dialog-title">
                            {"Use Google's location service?"}
                        </DialogTitle>
                        <DialogContent>
                            <DialogContentText id="alert-dialog-description">
                                Let Google help apps determine location. This means sending anonymous
                                location data to Google, even when no apps are running.
                            </DialogContentText>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={handleClose}>Disagree</Button>
                            <Button onClick={handleClose} autoFocus>
                                Agree
                            </Button>
                        </DialogActions>
                    </Dialog>
                </div>



            </div>
        );
    }


}


export default Dataset
