import React, { Component } from 'react';
import Alert from '@mui/material/Alert';
import Dataset from "../components/dataset/Dataset";
import CreateDataset from "../components/dataset/CreateDataset";

const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

class DatasetController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,

            hasDataset: false,
            datasetListJSON: JSON,

            panelStatus: PanelStatus.NoStatus,
            error: '',
        };

        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData();
    }

    async fetchData() {
        console.log("Fetch data")
        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) { throw new Error('Network response was not ok');}

            const result = await response.json();
            if (result.datasets.length === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    hasDataset : true,
                    datasetListJSON : result.datasets,
                });
            }
        } catch (error) {
            console.error(error);
            this.setState({
                error: error
            });
        }
    }

    async sendPOST(JSONObject) {
        // POST request using fetch with async/await
        const requestOptions = {
            method: 'POST',
            body: JSON.stringify(JSONObject)
        };
        const response = await fetch(this.state.serverUrl, requestOptions);
        if (!response.ok) {
            return PanelStatus.UnableToCreate
        }

        const data = await response.json();

        return PanelStatus.Success
    }

    //
    handleDatasetControllerPanelStatusChange = (statusCode) => {
        this.setState({
            datasetControllerPanelStatus : statusCode,
        })
    }

    handleCreateDataset = (stringifyJSON) => {
        try {
            const newObj = JSON.parse(stringifyJSON)
            console.log(JSON.stringify(newObj))

            // Send Data
            this.sendPOST(newObj).then(status => {
                console.log(status)
                this.setState(
                    {datasetUpdateStatus: status}
                )
            })

        } catch (e) {
            console.error(e)
            this.setState(
                {datasetUpdateStatus: PanelStatus.WrongJSON}
            )
        }
    };

    render() {
        const {isLoaded, datasetListJSON} = this.state

        if (!isLoaded) {
            return (
                <div className="Dataset">
                    <h1>Dataset Controller Panel</h1>
                    <br/>
                    <h2>Unable to Connect {this.state.serverUrl}...</h2>
                </div>
            );
        }

        const numberOfDataset = datasetListJSON.length
        const datasetElements = [];
        for (let i = 0; i < numberOfDataset; i++) {
            datasetElements.push(
                <Dataset key={i} datasetSequence={i} datasetOriginalJSON={datasetListJSON[i]}/>
            );
        }

        return (
            <div className="Dataset">
                <h1>Dataset Controller Panel</h1>

                <h2>There are {numberOfDataset} Datasets</h2>

                {datasetElements}

                <CreateDataset
                    handleCreateDataset={this.handleCreateDataset}
                    handlePanelStatusChange={this.handlePanelStatusChange}
                />

                <div className="Indicator">
                    {PanelStatusElement[this.state.panelStatus]}
                </div>
            </div>
        );
    }
}

export default DatasetController;
