import React, { Component } from 'react';
import CreateDataset from '../components/dataset/CreateDataset';
import GetDataset from '../components/dataset/GetDataset';
import DeleteDataset from "../components/dataset/DeleteDataset";

const DatasetUpdateStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

class Dataset extends Component {

    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',

            datasetConfigJSON: JSON,
            datasetStatusJSON: JSON,
            datasetOriginalJSON: JSON,

            datasetConfigUserInput: JSON.stringify(JSON),
            datasetUpdateStatus: DatasetUpdateStatus.NoStatus
        };

        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData();
    }

    async fetchData() {
        console.log("Fetch data")
        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            const size = result.items.length
            if (size === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    datasetOriginalJSON: result.items[0],
                    hasDataset : true
                });
            }
        } catch (error) {
            console.error(error);
            this.setState({
                error: error
            });
        }
    }

    render() {
        return (
            <div className="Dataset">
                <h1>Dataset Panel</h1>
                <GetDataset state={this.state}/>
                <DeleteDataset state={this.state}/>
                {/*<CreateDataset />*/}
            </div>
        );
    }
}

export default Dataset;
