import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import {modalStyle, DataType} from "./util";
import CodeEditor from '@uiw/react-textarea-code-editor';
import {Grid} from "@mui/material";

const CreateObject = ({   objectKind,
                          handleSendRequest
}) => {
    const [objectConfigInput, setObjectConfigInput] = useState('');
    const [modalOpen, setModalOpen] = useState(false);
    const httpMethod = 'POST';

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(objectConfigInput);
        handleSendRequest(httpMethod, DataType.YAMLString, objectConfigInput);
    };

    const handleModalOpen = () => {
        setModalOpen(true);
    };

    const handleModalClose = () => {
        setModalOpen(false);
    };

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        if (!file) {
            return;
        }

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                setObjectConfigInput(e.target.result)
                console.log('YAML Object:', e.target.result); // For debugging
            } catch (e) {
                console.error('Parsing error:', e);
            }
        };
        reader.readAsText(file);
    };

    const modalElement = (
        <div className="CreateObject">
            <Grid container spacing={2}>
                <Grid item lg={12}>
                    <Box sx={{
                        height: '30px',
                        fontSize: '24px',
                        fontWeight: 'bold',
                        width: '100%',}}>
                        Create a new {objectKind}
                    </Box>
                </Grid>
                <Grid item lg={8}>
                    <Box sx={{
                        height: '750px',
                        width: '100%',
                        overflow: 'scroll'
                    }}>
                        <CodeEditor
                            value={objectConfigInput}
                            language="yaml"
                            placeholder="Enter YAML Style Configuration or Upload Configuration File(.yaml/.yml)."
                            onChange={(evn) => setObjectConfigInput(evn.target.value)}
                            padding={15}
                            style={{
                                fontSize: '16px',
                                fontFamily: 'ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace',
                                minHeight: '100%',
                            }}
                        />
                    </Box>
                </Grid>

                <Grid item lg={4}>
                    <Grid container spacing={2}>
                        <Grid item lg={12}>
                            <Box>
                                <input type="file" onChange={handleFileUpload} accept=".yaml,.yml"/>
                            </Box>
                        </Grid>

                        <Grid item lg={6}>
                            <Button variant="outlined" onClick={handleSubmit}>
                                Create
                            </Button>
                        </Grid>
                        <Grid item lg={6}>
                            <Button variant="outlined" onClick={handleModalClose}>
                                Cancel
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>

            </Grid>
        </div>
    )

    return (
        <div className='Create-Object'>
            <Button variant="outlined" onClick={handleModalOpen} sx={{fontSize: '18px'}}>
                Create a new {objectKind}
            </Button>
            <Modal
                open={modalOpen}
                onClose={handleModalClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={modalStyle}>
                    {modalElement}
                </Box>
            </Modal>
        </div>
    )

}

export default CreateObject
