import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            datasetConfigUserInput: JSON.stringify(JSON),
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
    }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        e.preventDefault();
        this.props.handleCreateDataset(this.state.datasetUserInput)
    }

    render() {
        const {datasetOriginalJSON} = this.props.state;

        return (
            <div className="CreateDataset">
                <h2>Create Or Update Dataset</h2>

                <form onSubmit={this.handelSubmit}>
                    <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {JSON.stringify(datasetOriginalJSON)}

                                </textarea>
                    </label>
                    <input type="submit" value="Submit Dataset Config JSON"/>
                </form>


            </div>
        )
    }

}

export default CreateDataset
