import React, {Component} from "react";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import ReactJson from "react-json-view";
import Grid from "@mui/material/Grid";
import { styled } from '@mui/material/styles'
import DeleteObject from "./DeleteObject";
import {modalStyle} from "./util";
import "./ViewObject.css"


class ViewObject extends Component {
    constructor(props) {
        super(props)
        this.state = {
            objectKind: this.props.objectKind,
            objectName: this.props.objectName,
            objectOriginalJSON: this.props.objectOriginalJSON,

            modalOpen: false,
        };
    }

    handleModalOpen = () => {
        this.setState({modalOpen: true})
    }
    handleModalClose = () => {
        this.setState({modalOpen: false})
    }

    render() {
        const {objectName, objectKind, objectOriginalJSON} = this.state
        const {handleSendRequest} = this.props
        return (
            <div className='Modal-ViewObject'>
                <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                    Setting
                </Button>

                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={modalStyle}>
                        <Grid container spacing={2} className='Setting'>
                            <Grid container spacing={2} className='Title'>
                                <Grid item xs={12} >
                                    <Box sx={{ fontSize: '24px', height: '30px'}}>
                                        Configuration of {objectName} [{objectKind}]
                                    </Box>
                                </Grid>

                                <Grid xs={6} lg={3}>
                                    <Box sx={{
                                        height: '100%', // or '100%' depending on your needs
                                        overflowY: 'auto', // vertical scrollbar
                                        width: '100%', // optional, if you also want the box to take the full width
                                        // additional styles can go here
                                    }}>
                                        <ReactJson src={objectOriginalJSON}/>
                                    </Box>
                                </Grid>
                                <Grid xs={6} lg={3}>
                                    <Box>
                                        <DeleteObject
                                            objectKind={objectKind}
                                            objectName={objectName}
                                            handleSendRequest={handleSendRequest}
                                        />
                                    </Box>
                                </Grid>
                            </Grid>
                        </Grid>

                </Box>
                </Modal>
            </div>
        )
    }
}

export default ViewObject
