import React, {Component, useEffect, useState} from "react";
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

export const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

const alertModalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


const OperatorAlert = ({ panelStatus, errorMessage }) => {
    const [status, setStatus] = useState(PanelStatus.NoStatus);
    const [error, setError] = useState('');

    // useEffect will run when `panelStatus` changes
    useEffect(() => {
        console.log("Get a new Status: ", panelStatus)
        setStatus(panelStatus)

    }, [panelStatus, errorMessage]);

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setStatus(PanelStatus.NoStatus)
    };

    return (
        <Snackbar open={status !== PanelStatus.NoStatus} autoHideDuration={3000} onClose={handleClose}>
            <Alert onClose={handleClose} severity="success" sx={{width: '100%'}}>
                This is a success message!
            </Alert>
        </Snackbar>
    )
}
export default OperatorAlert
