import React, { Component } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import YAML from "yaml";
import {YAMLtoJSON} from "../util";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};

class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            httpMethod: 'POST',
            datasetConfigUserInput: YAML.stringify(YAML),

            createDatasetModalOpen: false,
        };

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetConfigUserInput : e.currentTarget.value}
        )
    }

    handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.datasetConfigUserInput)
        this.props.handleSendRequest(this.state.datasetConfigUserInput, this.state.httpMethod)
    }

    handleModalOpen = () => {
        this.setState({
            createDatasetModalOpen: true,
        })
    }
    handleModalClose = () => {
        this.setState({
            createDatasetModalOpen: false,
        })
    }


    render() {

        const modalElement = (
            <div className="CreateDataset">
                <h2>Create a new Dataset</h2>
                You can fill in these blanks
                {/*<div className="Required">*/}
                {/*    <TextField*/}
                {/*        required*/}
                {/*        id="outlined-required"*/}
                {/*        label="Dataset Name"*/}
                {/*        defaultValue=""*/}
                {/*        margin='dense'*/}
                {/*    />*/}

                {/*    <TextField*/}
                {/*        required*/}
                {/*        id="outlined-required"*/}
                {/*        label="Dataset Path"*/}
                {/*        fullWidth='true'*/}
                {/*        margin='dense'*/}
                {/*    />*/}
                {/*</div>*/}
                {/*<div className="Optional">*/}
                {/*    <TextField*/}
                {/*        id="outlined-multiline-static"*/}
                {/*        label="Credentials"*/}
                {/*        multiline*/}
                {/*        rows={4}*/}
                {/*        fullWidth='true'*/}
                {/*        margin='dense'*/}
                {/*    />*/}
                {/*</div>*/}


                Or put JSON Config Below:
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <textarea
                            rows='30' cols='50' onChange={this.handleChange}
                            spellCheck='false' defaultValue={this.state.datasetConfigUserInput}
                        />
                    </label>
                    <input type="submit" value="Submit DatasetController Config JSON"/>
                </form>
            </div>
        )


        return (
            <div className='Create-Dataset'>
                <Button variant="outlined" onClick={this.handleModalOpen}>
                    Create a New Dataset
                </Button>
                <Modal
                    open={this.state.createDatasetModalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={style}>
                        {modalElement}
                    </Box>
                </Modal>
            </div>
        )
    }
}

export default CreateDataset
