import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            datasetConfigUserInput: JSON.stringify(JSON)
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
    }

    // componentDidUpdate(prevProps) {
    //     if (prevProps.state.hasDataset !== this.props.state.hasDataset) {
    //         this.setState({ datasetConfigUserInput: JSON.stringify(this.props.state.datasetConfigJSON)});
    //     }
    // }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        e.preventDefault();
        this.props.handleCreateDataset(this.state.datasetUserInput)
    }

    render() {
        const {hasDataset, datasetConfigJSON} = this.props.state;

        if (!hasDataset) {
            return (
                <div className="CreateDataset">
                    <h2>Create Dataset</h2>
                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {this.state.datasetUserInput}
                                </textarea>
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>
                </div>
            )
        } else {
            return (
                <div className="CreateDataset">
                    <h2>Update Dataset</h2>
                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {JSON.stringify(datasetConfigJSON)}

                                </textarea>
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>
                </div>
            )
        }

    }

}

export default CreateDataset
