import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',
            datasetOriginalJSON: JSON,
            datasetUserInput: JSON.stringify(JSON),

            datasetUpdateStatus: DatasetUpdateStatus.NoStatus
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }

    async fetchData() {
        console.log("Fetch data")

        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            const size = result.items.length
            if (size === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    datasetOriginalJSON: result.items[0],
                    datasetUserInput: JSON.stringify(result.items[0]),
                    hasDataset : true
                });
            }
        } catch (error) {
            console.error(error);
        }
    }


    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        e.preventDefault();
        try {
            const newObj = JSON.parse(this.state.datasetUserInput)
            console.log(JSON.stringify(newObj))

            // Send Data
            this.sendData(newObj).then(status => {
                console.log(status)
                this.setState(
                    {datasetUpdateStatus: status}
                )
            })

        } catch (e) {
            console.error(e)
            this.setState(
                {datasetUpdateStatus: DatasetUpdateStatus.WrongJSON}
            )
        }
    }




    render() {
        const { error, isLoaded, datasetOriginalJSON} = this.state;
        if (error) {
            return  <div className="CreateDataset">
                        <h2>Create Or Update Dataset</h2>
                        Error: {error}
                    </div>;
        } else if (!isLoaded) {
            return <div className="CreateDataset">
                <h2>Create Or Update Dataset</h2>
                Loading...
            </div>;
        } else {
            return (
                <div className="CreateDataset">
                    <h2>Create Or Update Dataset</h2>

                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {JSON.stringify(datasetOriginalJSON)}
                                </textarea>
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>

                    {DatasetUpdateStatusElement[this.state.datasetUpdateStatus]}
                </div>
            )
        }
    }

}

export default CreateDataset
