import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',
            datasetOriginalJSON: JSON,
            datasetUserInput: '',
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData() {
        console.log("Fetch data")
        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        const size = result.items.length
                        if (size === 0) {
                            this.setState({
                                isLoaded: true,
                                datasetOriginalJSON: {},
                                hasDataset : false
                            });
                        } else {
                            this.setState({
                                isLoaded: true,
                                datasetOriginalJSON: result.items[0],
                                hasDataset : true
                            });
                        }
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }


    handleChange(e) {
        e.preventDefault()
        console.log(e.currentTarget.value)

        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        // Prevent the browser from reloading the page
        e.preventDefault();
        console.log('You clicked submit.')

        // Read the form data
        const form = e.target;
        const formData = new FormData(form);

        const formJson = Object.fromEntries(formData.entries());
        console.log(formJson);

    }

    render() {
        const { error, isLoaded, datasetOriginalJSON} = this.state;
        if (error) {
            return  <div className="CreateDataset">
                        <h2>Create Or Update Dataset</h2>
                        Error: {error}
                    </div>;
        } else if (!isLoaded) {
            return <div className="CreateDataset">
                <h2>Create Or Update Dataset</h2>
                Loading...
            </div>;
        } else {
            return (
                <div className="CreateDataset">
                    <h2>Create Or Update Dataset</h2>

                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                defaultValue={JSON.stringify(datasetOriginalJSON)}
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            />
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>

                </div>
            )
        }
    }

}

export default CreateDataset
