import React, { Component } from 'react';
import CreateDataset from '../components/dataset/CreateDataset';
import ShowDataset from '../components/dataset/ShowDataset';
import DeleteDataset from "../components/dataset/DeleteDataset";
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';

const DatasetUpdateStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const DatasetUpdateStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

class Dataset extends Component {

    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',

            datasetListJSON: JSON,

            // datasetConfigJSON: JSON,
            // datasetStatusJSON: JSON,
            // datasetOriginalJSON: JSON,

            datasetUpdateStatus: DatasetUpdateStatus.NoStatus
        };

        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData();
    }

    async fetchData() {
        console.log("Fetch data")
        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) { throw new Error('Network response was not ok');}

            const result = await response.json();
            if (result.datasets.length === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    hasDataset : true,

                    datasetListJSON : result.datasets,
                    // datasetOriginalJSON: result.items[0],
                    // datasetStatusJSON: result.items[0].status,
                    // datasetConfigJSON: result.items[0].datasetConfig,
                });
            }
        } catch (error) {
            console.error(error);
            this.setState({
                error: error
            });
        }
    }

    async sendPOST(JSONObject) {
        // POST request using fetch with async/await
        const requestOptions = {
            method: 'POST',
            body: JSON.stringify(JSONObject)
        };
        const response = await fetch(this.state.serverUrl, requestOptions);
        if (!response.ok) {
            return DatasetUpdateStatus.UnableToCreate
        }

        const data = await response.json();

        return DatasetUpdateStatus.Success
    }

    //

    handleCreateDataset = (stringifyJSON) => {
        try {
            const newObj = JSON.parse(stringifyJSON)
            console.log(JSON.stringify(newObj))

            // Send Data
            this.sendPOST(newObj).then(status => {
                console.log(status)
                this.setState(
                    {datasetUpdateStatus: status}
                )
            })

        } catch (e) {
            console.error(e)
            this.setState(
                {datasetUpdateStatus: DatasetUpdateStatus.WrongJSON}
            )
        }
    };

    render() {
        const {hasDataset, datasetListJSON} = this.state

        return (
            <div className="Dataset">
                <h1>Dataset Panel</h1>

                <h2>There are {datasetListJSON.length} Datasets</h2>

                {datasetListJSON.datasets.map(datasetOriginalJSON => (

                    <div>
                        <ShowDataset hasDataset={hasDataset}
                                     datasetConfigJSON={datasetOriginalJSON.datasetConfig}
                                     datasetStatusJSON={datasetOriginalJSON.status}
                        />
                        {/*<CreateDataset state={this.state} handleCreateDataset={this.handleCreateDataset}/>*/}
                        {/*<DeleteDataset state={this.state}/>*/}

                        <div className='DS JSON'>
                            <h3>Original Input JSON:</h3>
                            <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>
                        </div>

                    </div>
                ))}

                <div className="Indicator">
                    {DatasetUpdateStatusElement[this.state.datasetUpdateStatus]}
                </div>

            </div>
        );
    }
}

export default Dataset;
