import React, { Component } from 'react';
import CreateDataset from '../dataset/CreateDataset';
import ShowDataset from '../dataset/ShowDataset';
import DeleteDataset from "../dataset/DeleteDataset";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TableCell from "@mui/material/TableCell";
import TableRow from "@mui/material/TableRow";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class Dataset extends Component {
    constructor(props) {
        super(props);
        this.state = {
            datasetSequence : this.props.datasetSequence,
            datasetOriginalJSON : this.props.datasetOriginalJSON,

            datasetConfigJSON: this.props.datasetOriginalJSON.datasetConfig,
            datasetStatusJSON: this.props.datasetOriginalJSON.status,
        };

        this.handleChange = this.handleChange.bind(this)

    }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetConfigUserInput : e.currentTarget.value}
        )
    }



    render() {
        const {datasetSequence, datasetConfigJSON, datasetStatusJSON} = this.state

        return (
            <TableRow
                key={datasetConfigJSON.name}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
                <TableCell>{datasetSequence}</TableCell>
                <TableCell component="th" scope="row">{datasetConfigJSON.name}</TableCell>
                <TableCell align="right">{datasetStatusJSON.phase}</TableCell>
                <TableCell align="right">{datasetStatusJSON.boundedAlluxioCluster || '-'}</TableCell>


                <TableCell align="right">
                    <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                        Setting
                    </Button>
                </TableCell>







            </TableRow>



            // <div key={datasetSequence}>
            //
            //     <ShowDataset hasDataset={hasDataset}
            //                  datasetConfigJSON={datasetOriginalJSON.datasetConfig}
            //                  datasetStatusJSON={datasetOriginalJSON.status}
            //     />
            //
            //
            //     <div className='DS JSON'>
            //         <h3>Original Input JSON:</h3>
            //         <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>
            //     </div>
            //
            //     <DeleteDataset datasetConfigJSON={datasetConfigJSON} handleSendRequest={this.props.handleSendRequest}/>
            // </div>
        );
    }


}


export default Dataset
