import React, { Component } from 'react';
import CreateObject from "../components/util/CreateObject";
import Datasets from "../components/dataset/Datasets";
import OperatorAlert from "../components/alert/Alert";
import {AlertMethod} from "../components/alert/Alert";
import {sendRequest, getRequest, isJSONString} from "../components/util/util";

const serverUrl = '/dataset'

class DatasetController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isLoaded: false,

            datasetListJSON: JSON,

            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        };
    }

    componentDidMount() {
        this.interval = setInterval(() => {
            this.handleGetRequest()
        }, 2000);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    handleGetRequest = () =>  {
        // Call getRequest
        getRequest(serverUrl).then(statusCodeAndMsg => {
            if (statusCodeAndMsg[0] === AlertMethod.Success) {
                // TODO find out why it is always different, and not update list correctly.
                if (this.state.datasetListJSON !== statusCodeAndMsg[2]['datasets']) {
                    console.log("Different JSON File")
                } else {
                    console.log("Same JSON File")
                }

                this.setState({
                    isLoaded: true,
                    datasetListJSON : statusCodeAndMsg[2]['datasets'],
                });
                console.log(statusCodeAndMsg[2])
            } else {
                this.setState({
                    alertMethod : statusCodeAndMsg[0],
                    alertMessage : statusCodeAndMsg[1],
                })
            }
        })
    }

    // handleSendRequest is a func that will call  and update the panel status
    handleSendRequest = (stringifyJSON, httpMethod) =>  {
        // Check the str is JSON or not
        if (!isJSONString(stringifyJSON)) {
            this.setState({
                alertMethod : AlertMethod.Warning,
                alertMessage: 'This is not a JSON file.',
            })
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: {'Content-Type':'application/json'},
            body: stringifyJSON
        };

        // Call sendRequest
        sendRequest(requestOptions, serverUrl).then(statusCodeAndMsg => {
            this.setState({
                alertMethod : statusCodeAndMsg[0],
                alertMessage : statusCodeAndMsg[1],
            })
        })
    }

    handleCleanAlertStatus = () =>  {
        this.setState({
            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        })
    }

    render() {
        const {isLoaded, datasetListJSON} = this.state
        if (!isLoaded) {
            return (
                <div className="Dataset">
                    <h1>Dataset Controller Panel</h1>
                    <br/>
                    <h2>Connecting Endpoint: {serverUrl}</h2>
                </div>
            );
        }

        return (
            <div className="Dataset">
                <h1>Dataset Controller Panel</h1>
                <br/>
                <Datasets
                    datasetListJSON={datasetListJSON}
                    handleSendRequest={this.handleSendRequest}
                />
                <br/>
                <CreateObject
                    objectKind={'Dataset'}
                    handleSendRequest={this.handleSendRequest}
                />

                <OperatorAlert alertMethod={this.state.alertMethod}
                               alertMessage={this.state.alertMessage}
                               handleCleanAlertStatus={this.handleCleanAlertStatus}
                />
            </div>
        );
    }
}

export default DatasetController;
