import React, { Component } from 'react';
import DeleteObject from "../../util/DeleteObject";

import Button from '@mui/material/Button';
import TableCell from "@mui/material/TableCell";
import TableRow from "@mui/material/TableRow";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";

import ReactJson from 'react-json-view'

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class AlluxioCluster extends Component {
    constructor(props) {
        super(props);
        this.state = {
            sequence : this.props.sequence,
            originalJSON : this.props.originalJSON,

            name : this.props.originalJSON['alluxio-cluster-config'].name,
            statusJSON: this.props.originalJSON['status'],
            settingModalOpen: false,
        };
    }

    handleModalOpen = () => {
        this.setState({settingModalOpen: true})
    }
    handleModalClose = () => {
        this.setState({settingModalOpen: false})
    }

    render() {
        const {sequence,name, statusJSON, originalJSON} = this.state
        const {handleSendRequest} = this.props

        const settingModal = (
            <div key={sequence}>
                <ReactJson src={originalJSON} />

                <DeleteObject
                    objectKind='AlluxioCluster'
                    objectName={name}
                    handleSendRequest={handleSendRequest}
                />
            </div>
        )

        return (
            <TableRow
                key={name}
                sx={{'&:last-child td, &:last-child th': {border: 0}}}
            >
                <TableCell>{sequence}</TableCell>
                <TableCell component="th" scope="row">{name}</TableCell>
                <TableCell align="right">{statusJSON.phase}</TableCell>
                <TableCell align="right">
                    <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                        Setting
                    </Button>
                </TableCell>


                <Modal
                    open={this.state.settingModalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={style}>
                        {settingModal}
                    </Box>
                </Modal>

            </TableRow>
        );
    }
}


export default AlluxioCluster
