import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',
            datasetOriginalJSON: JSON,
            datasetUserInput: JSON.stringify(JSON),
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData() {
        console.log("Fetch data")
        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        const size = result.items.length
                        if (size === 0) {
                            this.setState({
                                isLoaded: true,
                                hasDataset : false
                            });
                        } else {
                            this.setState({
                                isLoaded: true,
                                datasetOriginalJSON: result.items[0],
                                datasetUserInput: JSON.stringify(result.items[0]),
                                hasDataset : true
                            });
                        }
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }


    handleChange(e) {
        e.preventDefault()

        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )

    }

    handelSubmit(e) {
        // Prevent the browser from reloading the page
        e.preventDefault();
        console.log('You clicked submit.')
        console.log(this.state.datasetUserInput)

        try {
            const newObj = JSON.parse(this.state.datasetUserInput)

            console.log(JSON.stringify(newObj))

        } catch (e) {
            console.error(e)
        }
    }

    sendData() {

    }

    render() {
        const { error, isLoaded, datasetOriginalJSON} = this.state;
        if (error) {
            return  <div className="CreateDataset">
                        <h2>Create Or Update Dataset</h2>
                        Error: {error}
                    </div>;
        } else if (!isLoaded) {
            return <div className="CreateDataset">
                <h2>Create Or Update Dataset</h2>
                Loading...
            </div>;
        } else {
            return (
                <div className="CreateDataset">
                    <h2>Create Or Update Dataset</h2>

                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {JSON.stringify(datasetOriginalJSON)}
                                </textarea>
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>

                </div>
            )
        }
    }

}

export default CreateDataset
