import React, { useState } from 'react';
import yaml from 'js-yaml'

function YamlUploader() {
    const [yamlObject, setYamlObject] = useState(null);

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        if (!file) {
            return;
        }

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const doc = yaml.load(e.target.result);
                setYamlObject(doc);
                console.log('YAML Object:', doc); // For debugging
            } catch (e) {
                console.error('Parsing error:', e);
            }
        };
        reader.readAsText(file);
    };

    return (
        <div>
            <input type="file" onChange={handleFileUpload} accept=".yaml,.yml" />
            {yamlObject && <pre>{JSON.stringify(yamlObject, null, 2)}</pre>}
        </div>
    );
}

export default YamlUploader;
