import { Component } from 'react';
import { useEffect } from 'react';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';

const DatasetUpdateStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const DatasetUpdateStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            hasDataset: false,
            error: '',
            datasetOriginalJSON: JSON,
            datasetUserInput: JSON.stringify(JSON),

            datasetUpdateStatus: DatasetUpdateStatus.NoStatus
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }




    async fetchData() {

        console.log("Fetch data")

        const response = await fetch(this.state.serverUrl)

        if (!response.ok) {
            throw new Error('Network response was not ok');
        }



        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        const size = result.items.length
                        if (size === 0) {
                            this.setState({
                                isLoaded: true,
                                hasDataset : false
                            });
                        } else {
                            this.setState({
                                isLoaded: true,
                                datasetOriginalJSON: result.items[0],
                                datasetUserInput: JSON.stringify(result.items[0]),
                                hasDataset : true
                            });
                        }
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }


    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    handelSubmit(e) {
        e.preventDefault();
        try {
            const newObj = JSON.parse(this.state.datasetUserInput)
            console.log(JSON.stringify(newObj))

            // Send Data
            this.sendData(newObj).then(status => {
                console.log(status)
                this.setState(
                    {datasetUpdateStatus: status}
                )
            })

        } catch (e) {
            console.error(e)
            this.setState(
                {datasetUpdateStatus: DatasetUpdateStatus.WrongJSON}
            )
        }
    }


    async sendData(JSONObject) {
        // POST request using fetch with async/await
        const requestOptions = {
            method: 'POST',
            body: JSON.stringify(JSONObject)
        };
        const response = await fetch(this.state.serverUrl, requestOptions);
        if (!response.ok) {
            return DatasetUpdateStatus.UnableToCreate
        }

        const data = await response.json();

        return DatasetUpdateStatus.Success
    }

    render() {
        const { error, isLoaded, datasetOriginalJSON} = this.state;
        if (error) {
            return  <div className="CreateDataset">
                        <h2>Create Or Update Dataset</h2>
                        Error: {error}
                    </div>;
        } else if (!isLoaded) {
            return <div className="CreateDataset">
                <h2>Create Or Update Dataset</h2>
                Loading...
            </div>;
        } else {
            return (
                <div className="CreateDataset">
                    <h2>Create Or Update Dataset</h2>

                    <form onSubmit={this.handelSubmit}>
                        <label>
                            <textarea
                                rows='30' cols='50' onChange={this.handleChange}
                                spellCheck='false'
                            >
                                {JSON.stringify(datasetOriginalJSON)}
                                </textarea>
                        </label>
                        <input type="submit" value="Submit Dataset Config JSON"/>
                    </form>

                    {DatasetUpdateStatusElement[this.state.datasetUpdateStatus]}
                </div>
            )
        }
    }

}

export default CreateDataset
