import React, {Component} from "react";
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';

export const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

const alertModalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class OperatorAlert extends Component {

    constructor(props) {
        super(props)
        this.state = {
            panelStatus: this.props.panelStatus,
            errorMessage: this.props.errorMessage,
            open: false
        };
    }

    handleClose = () => {
        if (reason === 'clickaway') {
            return;
        }

        this.setState({
            panelStatus: PanelStatus.NoStatus,
            errorMessage: ''
        })
    }

    render() {
        const {panelStatus, errorMessage, handleClose} = this.state.panelStatus

        return (
            <Snackbar open={panelStatus !== PanelStatus.NoStatus} autoHideDuration={6000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="success" sx={{width: '100%'}}>
                    This is a success message!
                </Alert>
            </Snackbar>
        )
    }
}

export default OperatorAlert
