import React, { Component } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import {YAMLtoJSON} from "./util";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};

/**
 *
 */
class CreateObject extends Component {
    constructor(props) {
        super(props)
        this.state = {
            objectKind: this.props.objectKind,

            httpMethod: 'POST',
            objectConfigInput: '',
            modalOpen: false,
        };
    }

    handleChange = (e) => {
        e.preventDefault()
        this.setState(
            { objectConfigInput : e.currentTarget.value}
        )
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // Convert to JSON
        this.props.handleSendRequest(YAMLtoJSON(this.state.objectConfigInput), this.state.httpMethod)
    }

    handleModalOpen = () => {
        this.setState({
            modalOpen: true,
        })
    }
    handleModalClose = () => {
        this.setState({
            modalOpen: false,
        })
    }

    render() {
        const modalElement = (
            <div className="CreateObject">
                <h2>Create a new {this.state.objectKind}</h2>
                Put YAML Config Below:
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <textarea
                            rows='30' cols='50' onChange={this.handleChange}
                            spellCheck='false' defaultValue={this.state.objectConfigInput}
                        />
                    </label>
                    <input type="submit" value="Create"/>
                </form>
            </div>
        )

        return (
            <div className='Create-Object'>
                <Button variant="outlined" onClick={this.handleModalOpen}>
                    Create a new {this.state.objectKind}
                </Button>
                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={style}>
                        {modalElement}
                    </Box>
                </Modal>
            </div>
        )
    }
}

export default CreateObject
