import React, {useEffect, useState} from "react";
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

import { useSelector, useDispatch } from 'react-redux';

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

export const AlertMethod =  {
    NoStatus : -1,
    Info : 0,
    Success : 1,
    Warning : 2,
    Error : 3,
}

const OperatorAlert = ({ alertMethod, alertMessage, handleCleanAlertStatus}) => {
    const [method, setMethod] = useState(AlertMethod.NoStatus);
    const [message, setMessage] = useState('');

    // useEffect will run when `panelStatus` changes
    useEffect(() => {
        if (alertMethod !== AlertMethod.NoStatus) {
            // console.log("Get a new Method Code: ", alertMethod, "Msg: ", alertMessage)
            setMethod(alertMethod)
            setMessage(alertMessage)
        }
    }, [alertMethod, alertMessage]);

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setMethod(AlertMethod.NoStatus)
        setMessage('')

        // Handle Clean Parent
        handleCleanAlertStatus()
    };

    const severity = [
        "info",
        "success",
        "warning",
        "error"
    ]

    return (
        <Snackbar open={method !== AlertMethod.NoStatus} autoHideDuration={3000} onClose={handleClose}>
            <Alert onClose={handleClose} severity={severity[method]} sx={{width: '100%'}}>
                {message}
            </Alert>
        </Snackbar>
    )
}
export default connect(mapStateToProps, mapDispatchToProps)(OperatorAlert)
