import React, {Component, useEffect, useState} from "react";
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

export const AlertStatus =  {
    NoStatus : -1,
    Info : 0,
    Success : 1,
    Warning : 2,
    Error : 3,
}

export const AlertMessage = {

}


const OperatorAlert = ({ panelStatus, displayMessage, handleCleanAlertStatus}) => {
    const [status, setStatus] = useState(AlertStatus.NoStatus);
    const [message, setMessage] = useState('');

    // useEffect will run when `panelStatus` changes
    useEffect(() => {
        if (panelStatus !== AlertStatus.NoStatus) {
            console.log("Get a new Status: ", panelStatus, "Msg: ", displayMessage)
            setStatus(panelStatus)
            setMessage(displayMessage)
        }
    }, [panelStatus, displayMessage]);

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setStatus(AlertStatus.NoStatus)
        setMessage('')

        // Handle Clean Parent
        handleCleanAlertStatus()
    };

    const severity = [
        "info",
        "success",
        "warning",
        "error"
    ]

    return (
        <Snackbar open={status !== AlertStatus.NoStatus} autoHideDuration={3000} onClose={handleClose}>
            <Alert onClose={handleClose} severity={severity[status]} sx={{width: '100%'}}>
                {message}
            </Alert>
        </Snackbar>
    )
}
export default OperatorAlert
