import React, { Component } from 'react';
import Alert from '@mui/material/Alert';
import Dataset from "../components/dataset/Dataset";
import CreateDataset from "../components/dataset/CreateDataset";

import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';

export const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

export function isJSON(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

class DatasetController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: '/dataset',
            isLoaded: false,

            hasDataset: false,
            datasetListJSON: JSON,

            panelStatus: PanelStatus.NoStatus,
            error: '',

            createDatasetModalOpen: false,
        };

        this.fetchData = this.fetchData.bind(this)
        this.sendRequest = this.sendRequest.bind(this)
    }

    componentDidMount() {
        this.fetchData();
    }

    // handleSendRequest is a func that will call  and update the panel status
    handleSendRequest = (stringifyJSON, httpMethod) =>  {
        // Check the str is JSON or not
        if (!isJSON(stringifyJSON)) {
            console.error('This is not a JSON file')
            this.setState({panelStatus : PanelStatus.WrongJSON})
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: {'Content-Type':'application/json'},
            body: stringifyJSON
        };

        // Call sendRequest
        this.sendRequest(requestOptions).then(statusCode => {
            console.log(statusCode)
            this.setState({
                panelStatus : statusCode,
            })
        })
    }

    // sendRequest is an async func that communicate with api server.
    async sendRequest(requestOptions) {
        // POST request using fetch with async/await
        try {
            const response = await fetch(this.state.serverUrl, requestOptions);
            if (!response.ok) {
                return PanelStatus.UnableToCreate
            }

            const data = await response.status;
            console.log(response)
            console.log(JSON.stringify(data))
            return PanelStatus.Success
        } catch (error) {
            console.log(error)
            return PanelStatus.UnableToCreate
        }
    }

    async fetchData() {
        // console.log("Fetch data")
        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) { throw new Error('Network response was not ok');}

            const result = await response.json();
            if (result.datasets.length === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    hasDataset : true,
                    datasetListJSON : result.datasets,
                });
            }
        } catch (error) {
            console.error(error);
            this.setState({
                error: error
            });
        }
    }

    handleModalOpen = () => {
        this.setState({
            createDatasetModalOpen: true,
        })
    }
    handleModalClose = () => {
        this.setState({
            createDatasetModalOpen: false,
        })
    }

    render() {
        const {isLoaded, datasetListJSON} = this.state
        if (!isLoaded) {
            return (
                <div className="Dataset">
                    <h1>Dataset Controller Panel</h1>
                    <br/>
                    <h2>Unable to Connect {this.state.serverUrl}...</h2>
                </div>
            );
        }

        const numberOfDataset = datasetListJSON.length
        const datasetElements = [];
        for (let i = 0; i < numberOfDataset; i++) {
            datasetElements.push(
                <Dataset key={i}
                         datasetSequence={i}
                         datasetOriginalJSON={datasetListJSON[i]}
                         handleSendRequest={this.handleSendRequest}
                />
            );
        }

        return (
            <div className="Dataset">
                <h1>Dataset Controller Panel</h1>

                <h2>There are {numberOfDataset} Datasets</h2>

                {datasetElements}

                <CreateDataset
                    handleSendRequest={this.handleSendRequest}
                />

                <div className="Indicator">
                    {PanelStatusElement[this.state.panelStatus]}
                </div>


                <div>
                    <Button onClick={this.handleModalOpen}>Open modal</Button>
                    <Modal
                        open={this.state.createDatasetModalOpen}
                        onClose={this.handleModalClose}
                        aria-labelledby="modal-modal-title"
                        aria-describedby="modal-modal-description"
                    >
                        <Box>
                            <Typography id="modal-modal-title" variant="h6" component="h2">
                                Text in a modal
                            </Typography>
                            <Typography id="modal-modal-description" sx={{mt: 2}}>
                                Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                            </Typography>
                        </Box>
                    </Modal>
                </div>

            </div>
        );
    }
}

export default DatasetController;
