import React, {Component} from "react";
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

export const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

const alertModalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class OperatorAlert extends Component {

    constructor(props) {
        super(props)
        this.state = {
            panelStatus: this.props.panelStatus,
            errorMessage: this.props.errorMessage,
            open: this.props.panelStatus !== PanelStatus.NoStatus
        };
    }

    handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        this.setState({
            panelStatus: PanelStatus.NoStatus,
            errorMessage: '',
            open: false
        })
    }

    render() {
        console.log("Get new status: ", this.state.panelStatus)
        const {open, errorMessage} = this.state.panelStatus

        return (
            <Snackbar open={open} autoHideDuration={3000} onClose={this.handleClose}>
                <Alert onClose={this.handleClose} severity="success" sx={{width: '100%'}}>
                    This is a success message!
                </Alert>
            </Snackbar>
        )
    }
}

export default OperatorAlert
