import { Component } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            httpMethod: 'POST',
            datasetConfigUserInput: JSON.stringify(JSON),
        };

        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(e) {
        e.preventDefault()
        this.setState(
            {datasetConfigUserInput : e.currentTarget.value}
        )
    }

    handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.datasetConfigUserInput)
        this.props.handleSendRequest(this.state.datasetConfigUserInput, this.state.httpMethod)
    }

    render() {
        return (
            <div className="CreateDataset">


                <h2>Create a new Dataset</h2>

                <div className="Required">
                    <TextField
                        required
                        id="outlined-required"
                        label="Dataset Name"
                        defaultValue=""
                        margin='normal'
                    />

                    <TextField
                        required
                        id="outlined-required"
                        label="Dataset Path"
                        defaultValue=""
                        fullWidth='true'
                        margin='normal'
                    />
                </div>
                <div className="Optional">


                </div>



                <form onSubmit={this.handleSubmit}>
                    <label>
                        <textarea
                            rows='30' cols='50' onChange={this.handleChange}
                            spellCheck='false' defaultValue={this.state.datasetConfigUserInput}
                        />
                    </label>
                    <input type="submit" value="Submit DatasetController Config JSON"/>
                </form>
            </div>
        )
    }
}

export default CreateDataset
