import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import {modalStyle, DataType} from "./util";
import Editor from 'react-simple-code-editor';
import Primis from 'prismjs'
import {Grid} from "@mui/material";


const CreateObject = ({   objectKind,
                          handleSendRequest
}) => {
    const [objectConfigInput, setObjectConfigInput] = useState('');
    const [modalOpen, setModalOpen] = useState(false);
    const httpMethod = 'POST';

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(objectConfigInput);
        handleSendRequest(httpMethod, DataType.YAMLString, objectConfigInput);
    };

    const handleModalOpen = () => {
        setModalOpen(true);
    };

    const handleModalClose = () => {
        setModalOpen(false);
    };

    const modalElement = (
        <div className="CreateObject">
            <Grid container spacing={2}>
                <Grid item lg={12}>
                    <Box sx={{
                        height: '3vh',
                        fontSize: '28px',
                        width: '100%',}}>

                        Create a new {objectKind}
                    </Box>
                </Grid>

                <Grid item lg={8}>
                    {/*<Editor*/}
                    {/*    onValueChange={code => setObjectConfigInput(code)}*/}
                    {/*    value={objectConfigInput}*/}
                    {/*    highlight={code => Prism.highlight(code, Prism.languages.yaml, 'yaml')}*/}
                    {/*    placeholder="Please enter YAML code."*/}
                    {/*    padding={10}*/}
                    {/*    style={{*/}
                    {/*        fontSize: '16px',*/}
                    {/*        fontFamily: 'ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace',*/}
                    {/*        height: '65vh',*/}
                    {/*        width: '50vw',*/}
                    {/*    }}*/}
                    {/*/>*/}
                    <Editor
                        value={objectConfigInput}
                        onValueChange={code => setObjectConfigInput(code)}
                        highlight={code => Primis.highlight(code, Primis.languages.yaml, 'yaml')}
                        padding={10}
                        style={{
                            fontFamily: '"Fira code", "Fira Mono", monospace',
                            fontSize: 12,
                        }}
                    />
                </Grid>

                <Grid item lg={4}>
                    <Grid container spacing={2}>
                        <Grid item lg={6}>
                            <Button variant="outlined" onClick={handleSubmit}>
                                Create
                            </Button>
                        </Grid>
                        <Grid item lg={6}>
                            <Button variant="outlined" onClick={handleModalClose}>
                                Cancel
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>

            </Grid>
        </div>
    )

    return (
        <div className='Create-Object'>
            <Button variant="outlined" onClick={handleModalOpen}>
                Create a new {objectKind}
            </Button>
            <Modal
                open={modalOpen}
                onClose={handleModalClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={modalStyle}>
                    {modalElement}
                </Box>
            </Modal>
        </div>
    )

}

export default CreateObject
