import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import {modalStyle, DataType} from "./util";
import CodeEditor from '@uiw/react-textarea-code-editor';
import {Grid} from "@mui/material";


const CreateObject = ({   objectKind,
                          handleSendRequest
}) => {
    const [objectConfigInput, setObjectConfigInput] = useState('');
    const [modalOpen, setModalOpen] = useState(false);
    const httpMethod = 'POST';

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(objectConfigInput);
        handleSendRequest(httpMethod, DataType.YAMLString, objectConfigInput);
    };

    const handleModalOpen = () => {
        setModalOpen(true);
    };

    const handleModalClose = () => {
        setModalOpen(false);
    };

    const modalElement = (
        <div className="CreateObject">
            <Box sx={{
                height: '3vh',
                fontSize: '18px',
                width: '100%',}}>
                Create a new {objectKind}
            </Box>
            <Grid container spacing={2}>
                <Grid item lg={8}>
                    <Box sx={{
                        height: '700px',
                        width: '100%',
                        overflow: 'scroll'
                    }}>
                        <CodeEditor
                            value={objectConfigInput}
                            language="yaml"
                            placeholder="Please enter YAML code."
                            onChange={(evn) => setObjectConfigInput(evn.target.value)}
                            padding={15}
                            style={{
                                fontSize: '16px',
                                fontFamily: 'ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace',
                                minHeight: '100%',
                            }}
                        />
                    </Box>
                </Grid>

                <Grid item lg={4}>
                    <Grid container spacing={2}>
                        <Grid item lg={6}>
                            <Button variant="outlined" onClick={handleSubmit}>
                                Create
                            </Button>
                        </Grid>
                        <Grid item lg={6}>
                        <Button variant="outlined" onClick={handleModalClose}>
                                Cancel
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>

            </Grid>
        </div>
    )

    return (
        <div className='Create-Object'>
            <Button variant="outlined" onClick={handleModalOpen} sx={{fontSize: '18px'}}>
                Create a new {objectKind}
            </Button>
            <Modal
                open={modalOpen}
                onClose={handleModalClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={modalStyle}>
                    {modalElement}
                </Box>
            </Modal>
        </div>
    )

}

export default CreateObject
