                {/*                        datasetConfigJSON={datasetOriginalJSON.datasetConfig}*/}
                {/*                        datasetStatusJSON={datasetOriginalJSON.status}*/}
                {/*            />*/}

                {/*             <div className='DS JSON'>*/}
                {/*                 <h3>Original Input JSON:</h3>*/}
                {/*                 <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>*/}
                {/*             </div>*/}
                {/*            <ReactJson src={datasetOriginalJSON} />*/}

                {/*            <DeleteObject*/}
                {/*                objectKind='Dataset'*/}
                {/*                objectName={datasetConfigJSON.name}*/}
                {/*                handleSendRequest={handleSendRequest}*/}
                {/*            />*/}

                {/*        </div>*/}

                {/*    </Box>*/}
                {/*</Modal>*/}
            </TableRow>
        );
    }
}


export default Dataset
