                {/*        />*/}
                {/*        /!*<CreateDataset state={this.state} handleCreateDataset={this.handleCreateDataset}/>*!/*/}
                {/*        /!*<DeleteDataset state={this.state}/>*!/*/}

                {/*        <div className='DS JSON'>*/}
                {/*            <h3>Original Input JSON:</h3>*/}
                {/*            <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*))}*/}

                {/*<div className="Indicator">*/}
                {/*    {DatasetUpdateStatusElement[this.state.datasetUpdateStatus]}*/}
                {/*</div>*/}



            </div>
        );
    }
}

export default Dataset;
