import React, {Component} from "react";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import ReactJson from "react-json-view";
import Grid from "@mui/material/Grid";
import { styled } from '@mui/material/styles'
import DeleteObject from "./DeleteObject";
import {JSONtoStringifyYAML, modalStyle} from "./util";
import "./ViewObject.css"


class ViewObject extends Component {
    constructor(props) {
        super(props)
        this.state = {
            objectKind: this.props.objectKind,
            objectName: this.props.objectName,
            objectConfigString: this.props.objectConfigString,
            objectStatusString: this.props.objectStatusString,

            modalOpen: false,
        };
    }

    handleModalOpen = () => {
        this.setState({modalOpen: true})
    }
    handleModalClose = () => {
        this.setState({modalOpen: false})
    }

    render() {
        const {objectName, objectKind, objectConfigString, objectStatusString} = this.state
        const {handleSendRequest} = this.props
        return (
            <div className='Modal-ViewObject'>
                <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                    Setting
                </Button>

                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={modalStyle}>
                        <div className='Setting'>
                            <Grid container spacing={2}>
                                <Grid item lg={12} sx={{height: '7vh'}}>
                                    <Box sx={{
                                        fontSize: '28px',
                                        width: '100%',
                                    }}>
                                        {objectName} [{objectKind}]
                                    </Box>
                                </Grid>

                                <Grid lg={4}>
                                    <Box sx={{
                                        fontSize: '20px',
                                        width: '100%',
                                        height: '3vh'
                                    }}>
                                        Configuration
                                    </Box>
                                    <Box sx={{
                                        height: '70vh',
                                        overflowY: 'auto',
                                        width: '100%',
                                    }}>
                                        <pre>
                                            {objectConfigString}
                                        </pre>
                                    </Box>
                                </Grid>


                                <Grid lg={4}>
                                    <Box sx={{
                                        fontSize: '20px',
                                        width: '100%',
                                        height: '3vh'
                                    }}>
                                        Status:
                                    </Box>
                                    <Box sx={{
                                        height: '70vh',
                                        overflowY: 'auto',
                                        width: '100%',
                                    }}>
                                        <pre>
                                            {objectStatusString}
                                        </pre>
                                    </Box>
                                </Grid>

                                <Grid lg={4}>
                                    <Box>
                                        <DeleteObject
                                            objectKind={objectKind}
                                            objectName={objectName}
                                            handleSendRequest={handleSendRequest}
                                        />
                                    </Box>
                                </Grid>
                            </Grid>

                        </div>
                </Box>
                </Modal>
            </div>
        )
    }
}

export default ViewObject
