import React, { Component } from 'react';
import OperatorAlert from "../components/alert/Alert";
import {AlertMethod} from "../components/alert/Alert";
import {sendRequest, getRequest, convertInputToStringifyJSON, FetchHeaders} from "../util/util";
import AlluxioClusters from "../components/alluxio_cluster/AlluxioClusters";
import CreateObject from "../util/CreateObject";

const serverUrl = '/api/alluxio_cluster'

class AlluxioController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            alluxioList: [],

            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        };
    }

    componentDidMount() {
        this.interval = setInterval(() => {
            this.handleGetRequest()
        }, 2000);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    handleGetRequest = () =>  {
        // Call getRequest
        getRequest(serverUrl).then(statusCodeAndMsg => {
            if (statusCodeAndMsg[0] === AlertMethod.Success) {
                const numberOfAlluxio = statusCodeAndMsg[2]['alluxio-clusters'].length
                const newAlluxioList = [];
                for (let i = 0; i < numberOfAlluxio; i++) {
                    newAlluxioList.push(statusCodeAndMsg[2]['alluxio-clusters'][i])
                    //console.log(statusCodeAndMsg[2]['alluxio-clusters'][i].status)
                }

                this.setState({alluxioList: newAlluxioList})
            } else {
                this.setState({
                    alertMethod : statusCodeAndMsg[0],
                    alertMessage : statusCodeAndMsg[1],
                })
            }
        })
    }

    // handleSendRequest is a func that will call sendRequest() and update the panel status
    handleSendRequest = (httpMethod, dataType, inputData) =>  {
        // Prep Body Data
        let requestBody = '';
        try {
            requestBody = convertInputToStringifyJSON(dataType, inputData)
        } catch (error) {
            this.setState({
                alertMethod : AlertMethod.Warning,
                alertMessage: 'This is not a Valid Input. ' + error,
            })
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: FetchHeaders,
            body: requestBody,
        };

        // Call sendRequest
        sendRequest(requestOptions, serverUrl).then(statusCodeAndMsg => {
            this.setState({
                alertMethod : statusCodeAndMsg[0],
                alertMessage : statusCodeAndMsg[1],
            })
        })
    }

    handleCleanAlertStatus = () =>  {
        this.setState({
            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        })
    }

    render() {
        return (
            <div className="Alluxio">
                <h1>Alluxio Controller Panel</h1>
                <br/>
                <CreateObject
                    objectKind={'Alluxio Cluster'}
                    handleSendRequest={this.handleSendRequest}
                />
                <br/>
                <AlluxioClusters
                    alluxioList={this.state.alluxioList}
                    handleSendRequest={this.handleSendRequest}
                />

                <OperatorAlert alertMethod={this.state.alertMethod}
                               alertMessage={this.state.alertMessage}
                               handleCleanAlertStatus={this.handleCleanAlertStatus}
                />
            </div>
        );
    }
}


export default AlluxioController;
