import React, { Component } from 'react';
import ShowDataset from '../dataset/ShowDataset';
import DeleteObject from "../../util/DeleteObject";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TableCell from "@mui/material/TableCell";
import TableRow from "@mui/material/TableRow";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class Dataset extends Component {
    constructor(props) {
        super(props);
        this.state = {
            datasetOriginalJSON : this.props.datasetOriginalJSON,

            datasetConfigJSON: this.props.datasetOriginalJSON.datasetConfig,
            datasetStatusJSON: this.props.datasetOriginalJSON.status,

            datasetSettingModalOpen: false,
        };
    }

    handleModalOpen = () => {
        this.setState({datasetSettingModalOpen: true})
    }
    handleModalClose = () => {
        this.setState({datasetSettingModalOpen: false})
    }

    render() {
        const {datasetConfigJSON, datasetStatusJSON, datasetOriginalJSON} = this.state
        const {handleSendRequest} = this.props

        return (
            <TableRow
                key={datasetConfigJSON.name}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
                <TableCell component="th" scope="row">{datasetConfigJSON.name}</TableCell>
                <TableCell align="right">{datasetStatusJSON.phase}</TableCell>
                <TableCell align="right">{datasetStatusJSON.boundedAlluxioCluster || '-'}</TableCell>
                <TableCell align="right">
                    <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                        Setting
                    </Button>
                </TableCell>

                <Modal
                    open={this.state.datasetSettingModalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={style}>
                        <div key={datasetConfigJSON.name}>
                           {/*<ShowDataset hasDataset={hasDataset}*/}
                           {/*             datasetConfigJSON={datasetOriginalJSON.datasetConfig}*/}
                           {/*             datasetStatusJSON={datasetOriginalJSON.status}*/}
                           {/* />*/}

                             <div className='DS JSON'>
                                 <h3>Original Input JSON:</h3>
                                 <pre>{JSON.stringify(datasetOriginalJSON, null, 2)}</pre>
                             </div>

                            <DeleteObject
                                objectKind='Dataset'
                                objectName={datasetConfigJSON.name}
                                handleSendRequest={handleSendRequest}
                            />

                        </div>

                    </Box>
                </Modal>
            </TableRow>
        );
    }
}


export default Dataset
