import React, { Component } from 'react';
import CreateDataset from "../components/dataset/CreateDataset";
import Datasets from "../components/dataset/Datasets";
import OperatorAlert from "../components/alert/Alert";
import {AlertStatus} from "../components/alert/Alert";
import {sendRequest} from "../components/util";


export function isJSON(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

class DatasetController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: '/dataset',
            isLoaded: false,

            datasetListJSON: JSON,


            panelStatus: AlertStatus.NoStatus,
            error: '',
        };

        this.fetchData = this.fetchData.bind(this)
        this.sendRequest = this.sendRequest.bind(this)
    }

    componentDidMount() {
        this.fetchData();
    }

    // handleSendRequest is a func that will call  and update the panel status
    handleSendRequest = (stringifyJSON, httpMethod) =>  {
        // Check the str is JSON or not
        if (!isJSON(stringifyJSON)) {
            console.error('This is not a JSON file')
            this.setState({
                panelStatus : AlertStatus.Warning,
                error: 'This is not a JSON file.',
            })
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: {'Content-Type':'application/json'},
            body: stringifyJSON
        };

        // Call sendRequest
        sendRequest(requestOptions, this.state.serverUrl).then(statusCodeAndMsg => {
            this.setState({
                panelStatus : statusCodeAndMsg[0],
                error : statusCodeAndMsg[1],
            })
        })
    }

    // sendRequest is an async func that communicate with api server.
    // async sendRequest(requestOptions, serverUrl) {
    //     // POST request using fetch with async/await
    //     try {
    //         const response = await fetch(serverUrl, requestOptions);
    //         if (!response.ok) {
    //             return [AlertStatus.Error, 'The Operator API returns not OK Response.']
    //         }
    //
    //         const data = response.status;
    //         console.log(response)
    //         console.log(JSON.stringify(data))
    //         return [AlertStatus.Success, 'Success!']
    //     } catch (error) {
    //         console.log(error)
    //         return [AlertStatus.Error, error]
    //     }
    // }

    async fetchData() {
        // console.log("Fetch data")
        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) { throw new Error('Network response was not ok');}

            const result = await response.json();
            this.setState({
                isLoaded: true,
                datasetListJSON : result.datasets,
            });
        } catch (error) {
            console.error(error);
            this.setState({
                isLoaded: false,
                error: error
            });
        }
    }


    handleCleanAlertStatus = () =>  {
        this.setState({
            panelStatus: AlertStatus.NoStatus,
            error: '',
        })
    }

    render() {
        const {isLoaded, datasetListJSON} = this.state
        if (!isLoaded) {
            return (
                <div className="Dataset">
                    <h1>Dataset Controller Panel</h1>
                    <br/>
                    <h2>Unable to Connect {this.state.serverUrl}...</h2>
                </div>
            );
        }

        return (
            <div className="Dataset">
                <h1>Dataset Controller Panel</h1>

                <Datasets
                    datasetListJSON={datasetListJSON}
                    handleSendRequest={this.handleSendRequest}
                />

                <CreateDataset
                    handleSendRequest={this.handleSendRequest}
                />

                <OperatorAlert panelStatus={this.state.panelStatus}
                               displayMessage={this.state.error}
                               handleCleanAlertStatus={this.handleCleanAlertStatus}
                />
            </div>
        );
    }
}

export default DatasetController;
