import { Component } from 'react';
import dataset from "../../pages/dataset";

interface Dataset {
    name: string,
    path: string,
    credentials?: Map<string, string>
    status?: Map<string, string>
}
const initalDatasetObject : Dataset = {
    name: '',
    path: '',
}

class GetDataset extends Component {
    state = {
        serverUrl: 'http://localhost:5220/dataset',
        dataset: initalDatasetObject,
        isLoaded: false,
        error: '',
        stringifiesJSON: JSON,
    };

    componentDidMount() {
        this.checkConnection()
        this.fetchData();
    }

    checkConnection() {
        try {
            const response = fetch(this.state.serverUrl)
            console.log(response.then())

        } catch (error) {
            console.log(error);
        }
    }

    fetchData() {
        console.log("Fetch data")
        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log(result.items[0]);

                        let newStatusMap = new Map<string, string>
                        newStatusMap.set("boundedAlluxioCluster", result.items[0].status.boundedAlluxioCluster)
                        newStatusMap.set("phase", result.items[0].status.phase)
                        let currentDatasetObject: Dataset = {
                            name: result.items[0].name,
                            path: result.items[0].path,
                            status: newStatusMap,
                        }

                        this.setState({
                            isLoaded: true,
                            dataset: currentDatasetObject,
                            stringifiesJSON: result.items[0].toJSON
                        });
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }

    render() {
        const { error, isLoaded, dataset} = this.state;
        if (error) {
            return <div className="GetDataset">Error: {error}</div>;
        } else if (!isLoaded) {
            return <div className="GetDataset">Loading...</div>;
        } else {
            return (
                <div className="GetDataset">

                    <div className='DS Name'>
                        <h3>Data Set Name:</h3>
                        {dataset.name}
                    </div>

                    <div className='DS Path'>
                        <h3>Data Set Status:</h3>
                        {dataset.path}
                    </div>

                    <div className='DS Status'>
                        <h3>Data Set Status:</h3>
                        <h4>boundedAlluxioCluster: </h4>
                        {dataset.status?.get('boundedAlluxioCluster')}
                        <h4>phase: </h4>
                        {dataset.status?.get('phase')}
                    </div>

                    <div className='DS JSON'>
                        <h3>Original JSON:</h3>
                        {this.state.stringifiesJSON}
                    </div>
                </div>
            );
        }
    }
}

export default GetDataset
