import React, {Component} from "react";
import Alert from "@mui/material/Alert";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";

export const PanelStatus =  {
    NoStatus : 0,
    Success : 1,
    WrongJSON : 2,
    UnableToCreate : 3,
}

const PanelStatusElement = [
    <></>,
    <Alert variant="outlined" severity="success">
        Success!
    </Alert>,
    <Alert variant="outlined" severity="warning">
        Unable to Create JSON Object, check input!
    </Alert>,
    <Alert variant="outlined" severity="error">
        Unable to Create!
    </Alert>
]

const alertModalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '75%',
    height: '80%',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    p: 4,
};


class OperatorAlert extends Component {

    constructor(props) {
        super(props)
        this.state = {
            panelStatus: this.props.panelStatus,
            errorMessage: this.props.errorMessage,
        };
    }

    handleModalClose = () => {
        this.setState({
            panelStatus: PanelStatus.NoStatus,
            errorMessage: ''
        })
    }

    render() {
        const {panelStatus} = this.state.panelStatus

        return (
            <Modal
                open={panelStatus}
                onClose={this.handleModalClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <div className="Indicator">
                        {PanelStatusElement[panelStatus]}
                    </div>
                </Box>
            </Modal>
        )
    }
}

export default OperatorAlert
