import React, { Component } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import {modalStyle, DataType} from "./util";
import CodeEditor from '@uiw/react-textarea-code-editor';

/**
 *
 */
class CreateObject extends Component {
    constructor(props) {
        super(props)
        this.state = {
            objectKind: this.props.objectKind,

            httpMethod: 'POST',
            objectConfigInput: '',
            modalOpen: false,
        };
    }

    handleChange = (e) => {
        e.preventDefault()
        this.setState({ objectConfigInput : e.currentTarget.value})
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.objectConfigInput)
        //this.props.handleSendRequest(this.state.httpMethod, DataType.YAMLString, this.state.objectConfigInput)
    }

    handleModalOpen = () => {
        this.setState({modalOpen: true})
    }
    handleModalClose = () => {
        this.setState({modalOpen: false})
    }

    render() {
        const modalElement = (
            <div className="CreateObject">
                <h2>Create a new {this.state.objectKind}</h2>
                <CodeEditor
                    value={this.state.objectConfigInput}
                    language="yaml"
                    placeholder="Please enter YAML code."
                    onChange={(evn) => this.setState(
                        {objectConfigInput: evn.target.value}
                    )}
                    padding={15}
                    style={{
                        fontSize: '20px',
                        fontFamily: 'ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace',
                        height: '60vh',
                        width: '50vw',
                    }}
                />
                <Button variant="outlined" onClick={this.handleModalOpen}>
                    Create
                </Button>

                {/*<h2>Create a new {this.state.objectKind}</h2>*/}
                {/*Put YAML Config Below:*/}
                {/*<form onSubmit={this.handleSubmit}>*/}
                {/*    <label>*/}
                {/*        <textarea*/}
                {/*            rows='30' cols='50' onChange={this.handleChange}*/}
                {/*            spellCheck='false' defaultValue={this.state.objectConfigInput}*/}
                {/*        />*/}
                {/*    </label>*/}
                {/*    <input type="submit" value="Create"/>*/}
                {/*</form>*/}
            </div>
        )

        return (
            <div className='Create-Object'>
                <Button variant="outlined" onClick={this.handleModalOpen}>
                    Create a new {this.state.objectKind}
                </Button>
                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={modalStyle}>
                        {modalElement}
                    </Box>
                </Modal>
            </div>
        )
    }
}

export default CreateObject
