import { Component } from 'react';


class CreateDataset extends Component {
    constructor(props) {
        super(props)
        this.state = {
            serverUrl: 'http://localhost:5220/dataset',
            isLoaded: false,
            error: '',
            datasetOriginalJSON: JSON,
            datasetUserInput: '',
        };

        this.handleChange = this.handleChange.bind(this)
        this.handelSubmitRequest = this.handelSubmitRequest.bind(this)
        this.fetchData = this.fetchData.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData() {
        console.log("Fetch data")
        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        console.log(JSON.stringify(result.items[0]));

                        this.setState({
                            isLoaded: true,
                            datasetOriginalJSON: result.items[0]
                        });
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }


    handleChange(e) {
        e.preventDefault()
        console.log(e.currentTarget.value)
    }

    handelSubmitRequest(e) {
        e.preventDefault()
        console.log('You clicked submit.');

        console.log(JSON.stringify(this.state.datasetUserInput))

        this.setState(
            {datasetUserInput : e.currentTarget.value}
        )
    }

    render() {
        console.log(JSON.stringify(this.state.datasetOriginalJSON))
        const defaultJSONText = JSON.stringify(this.state.datasetOriginalJSON)
        return (
            <div>
                <h2>Create Or Update Dataset</h2>
                <form onSubmit={this.handelSubmitRequest}>
                    <label>
                        <textarea defaultValue={defaultJSONText} cols='50pt' rows='50pt'
                                  onChange={this.handleChange}/>
                    </label>
                    <input type="submit" value="Submit JSON"/>
                </form>
            </div>
        )
    }

}

export default CreateDataset
