import React, { Component } from 'react';
import OperatorAlert from "../components/alert/Alert";
import {AlertMethod} from "../components/alert/Alert";
import {sendRequest, YAMLtoJSON, getRequest, isJSONString} from "../components/util/util";
import AlluxioClusters from "../components/alluxio_cluster/AlluxioClusters";
import CreateObject from "../components/util/CreateObject";

const serverUrl = '/alluxio_cluster'

class AlluxioController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isLoaded: false,

            alluxioListJSON: JSON,

            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        };
    }

    componentDidMount() {
        this.interval = setInterval(() => {
            this.handleGetRequest()
        }, 2000);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    handleGetRequest = () =>  {
        // Call getRequest
        getRequest(serverUrl).then(statusCodeAndMsg => {
            if (statusCodeAndMsg[0] === AlertMethod.Success) {
                this.setState({
                    isLoaded: true,
                    alluxioListJSON: statusCodeAndMsg[2]['alluxio-clusters'],
                });
            } else {
                this.setState({
                    alertMethod : statusCodeAndMsg[0],
                    alertMessage : statusCodeAndMsg[1],
                })
            }
        })
    }

    // handleSendRequest is a func that will call sendRequest() and update the panel status
    handleSendRequest = (stringifyYAML, httpMethod) =>  {
        const stringifyJSON = YAMLtoJSON(stringifyYAML)

        // Check the str is JSON or not
        if (!isJSONString(stringifyJSON)) {
            this.setState({
                alertMethod : AlertMethod.Warning,
                alertMessage: 'This is not a Valid Input',
            })
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: {'Content-Type':'application/json'},
            body: stringifyJSON
        };

        // Call sendRequest
        sendRequest(requestOptions, serverUrl).then(statusCodeAndMsg => {
            this.setState({
                alertMethod : statusCodeAndMsg[0],
                alertMessage : statusCodeAndMsg[1],
            })
        })
    }

    handleCleanAlertStatus = () =>  {
        this.setState({
            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        })
    }

    render() {
        const {isLoaded, alluxioListJSON} = this.state
        if (!isLoaded) {
            return (
                <div className="Alluxio">
                    <h1>Alluxio Controller Panel</h1>
                    <br/>
                    <h2>Connecting Endpoint: {serverUrl}</h2>
                </div>
            );
        }

        return (
            <div className="Alluxio">
                <h1>Alluxio Controller Panel</h1>
                <br/>
                <AlluxioClusters
                    alluxioListJSON={alluxioListJSON}
                    handleSendRequest={this.handleSendRequest}
                />
                <br/>
                <CreateObject
                    objectKind={'Alluxio Cluster'}
                    handleSendRequest={this.handleSendRequest}
                />

                <OperatorAlert alertMethod={this.state.alertMethod}
                               alertMessage={this.state.alertMessage}
                               handleCleanAlertStatus={this.handleCleanAlertStatus}
                />
            </div>
        );
    }
}


export default AlluxioController;
