import React, { Component } from 'react';
import CreateObject from "../util/CreateObject";
import Datasets from "../components/dataset/Datasets";
import OperatorAlert from "../components/alert/Alert";
import {AlertMethod} from "../components/alert/Alert";
import {sendRequest, getRequest, convertInputToStringifyJSON, FetchHeaders} from "../util/util";

const serverUrl = '/api/dataset'

class DatasetController extends Component {
    constructor(props) {
        super(props)
        this.state = {
            datasetList: [],

            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        };
    }

    componentDidMount() {
        this.interval = setInterval(() => {
            this.handleGetRequest()
        }, 2000);
    }

    componentWillUnmount() {
        clearInterval(this.interval);
    }

    handleGetRequest = () =>  {
        // Call getRequest
        getRequest(serverUrl).then(statusCodeAndMsg => {
            if (statusCodeAndMsg[0] === AlertMethod.Success) {
                const numberOfDataset = statusCodeAndMsg[2]['datasets'].length
                const newDatasetList = [];
                for (let i = 0; i < numberOfDataset; i++) {
                    newDatasetList.push(statusCodeAndMsg[2]['datasets'][i])
                }

                this.setState({datasetList : newDatasetList})

            } else {
                this.setState({
                    alertMethod : statusCodeAndMsg[0],
                    alertMessage : statusCodeAndMsg[1],
                })
            }
        })
    }

    // handleSendRequest is a func that will call  and update the panel status
    handleSendRequest = (httpMethod, dataType, inputData) =>  {
        // Prep Body Data
        let requestBody = '';
        try {
             requestBody = convertInputToStringifyJSON(dataType, inputData)
        } catch (error) {
            this.setState({
                alertMethod : AlertMethod.Warning,
                alertMessage: 'This is not a Valid Input. ' + error,
            })
            return
        }

        // Prep Http request
        const requestOptions = {
            method: httpMethod,
            headers: FetchHeaders,
            body: requestBody,
        };

        // Call sendRequest
        sendRequest(requestOptions, serverUrl).then(statusCodeAndMsg => {
            this.setState({
                alertMethod : statusCodeAndMsg[0],
                alertMessage : statusCodeAndMsg[1],
            })
        })
    }

    handleCleanAlertStatus = () =>  {
        this.setState({
            alertMethod: AlertMethod.NoStatus,
            alertMessage: '',
        })
    }

    render() {
        return (
            <div className="Dataset">
                <h1>Dataset Controller Panel</h1>
                <br/>
                <CreateObject
                    objectKind={'Dataset'}
                    handleSendRequest={this.handleSendRequest}
                />
                <br/>
                <Datasets
                    datasetList={this.state.datasetList}
                    handleSendRequest={this.handleSendRequest}
                />

                <OperatorAlert alertMethod={this.state.alertMethod}
                               alertMessage={this.state.alertMessage}
                               handleCleanAlertStatus={this.handleCleanAlertStatus}
                />
            </div>
        );
    }
}

export default DatasetController;
