import React, {Component} from "react";
import {Button, Modal, Box, Grid, Typography} from "@mui/material";
import DeleteObject from "./DeleteObject";
import {modalStyle} from "./util";


class ViewObject extends Component {
    constructor(props) {
        super(props)
        this.state = {
            objectKind: this.props.objectKind,
            objectName: this.props.objectName,
            objectConfigString: this.props.objectConfigString,
            objectStatusString: this.props.objectStatusString,

            modalOpen: false,
        };
    }

    handleModalOpen = () => {
        this.setState({modalOpen: true})
    }
    handleModalClose = () => {
        this.setState({modalOpen: false})
    }

    render() {
        const {objectName, objectKind, objectConfigString, objectStatusString} = this.state
        const {handleSendRequest} = this.props
        return (
            <div className='Modal-ViewObject'>
                <Button variant="outlined" onClick={this.handleModalOpen} size="small">
                    Setting
                </Button>

                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleModalClose}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <Box sx={modalStyle}>
                        <div className='Setting'>
                            <Grid container spacing={2}>
                                <Grid item lg={12} sx={{height: '7vh'}}>
                                    <Box sx={{
                                        fontSize: '28px',
                                        width: '100%',
                                    }}>
                                        {objectName} [{objectKind}]
                                    </Box>
                                </Grid>

                                <Grid lg={4}>
                                    <Box sx={{
                                        fontSize: '20px',
                                        width: '100%',
                                        height: '3vh'
                                    }}>
                                        Configuration
                                    </Box>
                                    <Box sx={{
                                        height: '70vh',
                                        overflowY: 'auto',
                                        width: '100%',
                                    }}>
                                        <Typography
                                            component="pre"
                                            sx={{
                                                whiteSpace: 'pre-wrap',
                                                overflowX: 'auto',
                                                backgroundColor: '#f0f0f0',
                                                padding: '10px',
                                                borderRadius: '4px',
                                                fontFamily: 'monospace',
                                            }}
                                        >
                                            {objectConfigString}
                                        </Typography>
                                    </Box>
                                </Grid>


                                <Grid lg={4}>
                                    <Box sx={{
                                        fontSize: '20px',
                                        width: '100%',
                                        height: '3vh'
                                    }}>
                                        Status
                                    </Box>
                                    <Box sx={{
                                        height: '70vh',
                                        overflowY: 'auto',
                                        width: '100%',
                                    }}>
                                        <Typography
                                            component="pre"
                                            sx={{
                                                whiteSpace: 'pre-wrap',
                                                overflowX: 'auto',
                                                backgroundColor: '#f0f0f0',
                                                padding: '10px',
                                                borderRadius: '4px',
                                                fontFamily: 'monospace',
                                            }}
                                        >
                                            {objectStatusString}
                                        </Typography>
                                    </Box>
                                </Grid>

                                <Grid lg={4}>
                                    <Box>
                                        <DeleteObject
                                            objectKind={objectKind}
                                            objectName={objectName}
                                            handleSendRequest={handleSendRequest}
                                        />
                                    </Box>
                                </Grid>
                            </Grid>

                        </div>
                </Box>
                </Modal>
            </div>
        )
    }
}

export default ViewObject
