import { Component } from 'react';



class GetDataset extends Component {
    state = {
        serverUrl: 'http://localhost:5220/dataset',
        isLoaded: false,
        error: '',
        datasetOriginalJSON: JSON,
    };

    componentDidMount() {
        this.checkConnection()
        this.fetchData();
    }

    checkConnection() {
        try {
            const response = fetch(this.state.serverUrl)
            console.log(response.then())

        } catch (error) {
            console.log(error);
        }
    }

    async fetchData() {
        console.log("Fetch data")

        try {
            const response = await fetch(this.state.serverUrl)
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            const size = result.items.length
            if (size === 0) {
                this.setState({
                    isLoaded: true,
                    hasDataset : false
                });
            } else {
                this.setState({
                    isLoaded: true,
                    datasetOriginalJSON: result.items[0],
                    hasDataset : true
                });
            }
        } catch (error) {
            console.error(error);
        }
    }

    fetchData() {
        console.log("Fetch data")
        try {
            fetch(this.state.serverUrl)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                            isLoaded: true,
                            datasetOriginalJSON: result.items[0]
                        });
                    },
                    (error) => {
                        this.setState({
                            isLoaded: false,
                            error
                        });
                    }
                )
        } catch (error) {
            console.error(error);
        }
    }

    render() {
        const { error, isLoaded, dataset} = this.state;
        if (error) {
            return <div className="GetDataset">Error: {error}</div>;
        } else if (!isLoaded) {
            return <div className="GetDataset">Loading...</div>;
        } else {
            return (
                <div className="GetDataset">

                    <div className='DS Name'>
                        <h3>Data Set Name:</h3>
                        {dataset.name}
                    </div>

                    <div className='DS Path'>
                        <h3>Data Set Status:</h3>
                        {dataset.path}
                    </div>

                    <div className='DS Status'>
                        <h3>Data Set Status:</h3>
                        {/*<h4>boundedAlluxioCluster: </h4>*/}
                        {/*{dataset.status?.get('boundedAlluxioCluster')}*/}
                        {/*<h4>phase: </h4>*/}
                        {/*{dataset.status?.get('phase')}*/}
                    </div>

                    <div className='DS JSON'>
                        <h3>Original JSON:</h3>
                        <>{JSON.stringify(this.state.datasetOriginalJSON)}</>

                    </div>

                </div>
            );
        }
    }
}

export default GetDataset
